import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Link, useNavigate } from 'react-router-dom'

import { useState } from 'react';
import axios from 'axios';

function Post() {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState('')

  const navigate = useNavigate()

  const login = async (e) => {
    e.preventDefault()
    try {
      await axios.post('http://localhost:3000/user/login', {
        USERNAME: username,
        PASSWORD: password,
      })
      .then((res) => {
        const response = res.data.loginResponse
        if(response.code === 200) {
          const jsonData = JSON.stringify(response.data.user)
          console.log(jsonData)
          localStorage.setItem('user', jsonData)
          navigate('/')
          window.location.reload();
        } else {
          setMsg(response.message)
          console.log("Response", res)
        }
      })
    } catch (error) {
      if(error.response){
        console.error("Error :",error.response)
      }
    }

  }
  return (
    <div className='d-flex justify-content-center align-items-center text-items-center bg-light-subtle' style={{width: '100%', height: '100%'}}>
      <div className="container d-flex justify-content-center align-items-center mt-lg-5 p-lg-3 bg-success bg-opacity-50" style={{ width: '1280px', }}>
    <Form style={{ width: '500px' }} onSubmit={login}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Username</Form.Label>
        <Form.Control value={username} onChange={(e) => setUsername(e.target.value)} type="text" placeholder="Enter Username..." />
        <Form.Text className="text-muted">
          Input Your Username
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Enter Password..." />
        <Form.Text className="text-muted">
          Input Your Password
        </Form.Text>
      </Form.Group>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Button variant="success" type="submit">
          Login
        </Button>
        <Link to={'/signup'}>Sign up</Link>
      </div>
    </Form>
    </div>
    </div>
  )
}

export default Post;
